import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
}
<h1 class="welcome-title">Welcome to Our Application!</h1>
<p class="welcome-message">We're glad to have you here. Explore the features we offer:</p>

<div class="image-placeholder">
    <!-- Placeholder for the image -->
    <img src="path/to/your/image.jpg" alt="Description of the image" class="welcome-image" />
</div>

<div class="feature-list">

</div>

<div class="getting-started-list">
    <h2>Get Started</h2>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque facilisis sollicitudin ante.</p>
    <button class="continue-button">Continue</button>
</div>
