import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
}
<h1 class="welcome-title">Welcome to Our Application!</h1>
<p class="welcome-message">We're glad to have you here. Explore the features we offer:</p>
<div class="feature-list">

</div>
<p class="getting-started">To help you get started, we've also set up:</p>
<div class="getting-started-list">
  <ul>
    <li><strong>Client-side navigation</strong>. For example, click <em>Counter</em> then <em>Back</em> to return here.</li>
    <li><strong>Angular CLI integration</strong>. In development mode, there's no need to run <code>ng serve</code>. It runs in the background automatically.</li>
    <li><strong>Efficient production builds</strong>. In production mode, development-time features are disabled.</li>
  </ul>
</div>
<p class="clientapp-info">The <code>ClientApp</code> subdirectory is a standard Angular CLI application. You can run any <code>ng</code> command or use <code>npm</code> to install extra packages.</p>
